<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$respuesta = array();		$consulta = "SELECT *  FROM compras_detalle WHERE id_compras = '{$_GET["id_compras"]}'";		$result = mysqli_query( $link, $consulta ) 	or die("Error al ejecutar consulta: $consulta".mysqli_error($link));			while($fila = mysqli_fetch_assoc($result)) {				$respuesta["productos"][] = $fila ;			}			print(json_encode($respuesta));	?>