<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$respuesta = array();		$consulta = "SELECT 	id_proveedores ,	id_productos ,	productos.piezas,	cantidad,	descripcion AS descripcion_productos,	precio AS costo_proveedor,	existencia_productos 	  	FROM compras_detalle	LEFT JOIN productos USING (id_productos)	LEFT JOIN compras USING (id_compras)	LEFT JOIN proveedores USING (id_proveedores)		WHERE id_compras = '{$_GET["id_compras"]}'";		$result = mysqli_query( $link, $consulta ) 	or die("Error al ejecutar consulta: $consulta".mysqli_error($link));			while($fila = mysqli_fetch_assoc($result)) {				$respuesta["compra"] = $fila ;		$respuesta["productos"][] = $fila ;			}			print(json_encode($respuesta));	?>