<?php	header('Content-Type: application/json');	include("../conexi.php");	$link=Conectarse();		$respuesta = array();		$tabla = $_GET["tabla"];	$id_campo = $_GET["id_campo"];	$id_valor = $_GET["id_valor"];	//$mensaje_error = $_GET["mensaje_error"];	$mensaje_error = "No encontrado";			//$query_complete = "SELECT $campo FROM $tabla  ";	//$query_complete = "SELECT $valor, $etiqueta,  FROM $tabla WHERE $etiqueta LIKE '%" . $q . "%'";	$consulta = "SELECT *  FROM $tabla WHERE $id_campo = '$id_valor'";		$result = mysqli_query( $link, $consulta ) 	or die("Error al ejecutar consulta: $consulta".mysqli_error($link));		$count_rows = mysqli_num_rows($result);	$count = 0;	while($row = mysqli_fetch_assoc($result)) {		$count++;		$respuesta["data"] = $row ;			}		$respuesta["count_rows"] = "$count_rows" ;			$respuesta["mensaje"] =  $count_rows < 1 ? $mensaje_error : "OK";	$respuesta["encontrado"] =  $count_rows < 1 ? 0 : 1;			print(json_encode($respuesta));	?>